 class Agenda extends MY_Controller{
  function __construct(){
      parent::__construct();
      $this->load->model('Agenda_model','model');
      if ($this->cl->acl("agenda")==false) {
        redirect("errors/er403");
      }
  }

 function _rules(){
		$this->form_validation->set_rules('id_agenda', 'id_agenda', 'trim');
		$this->form_validation->set_rules('agenda', 'agenda', 'trim|xss_clean|required');
    $this->form_validation->set_rules('date', 'Waktu', 'trim|xss_clean|required');
		$this->form_validation->set_rules('desc', 'Perihal', 'trim|xss_clean|required');
		$this->form_validation->set_error_delimiters('<span class="text-danger">', '</span>');
  }

 function index(){
    $this->layouts->set_title('Agenda');
    $this->layouts->view(config_item("cpanel").'content/agenda/index');
  }

function json() {
    header('Content-Type: application/json');
    echo $this->model->json();
}


 function detail($id){
    if($row=$this->model->get_where($id)){
      $this->layouts->set_title('Agenda');
        $data=array('layout_title' => 'Agenda',
										'id_agenda' => $row->id_agenda,
										'agenda' => $row->agenda,
