 class Berita extends MY_Controller{
  function __construct(){
      parent::__construct();
      $this->load->model('Berita_model','model');
      if ($this->cl->acl("berita")==false) {
        redirect("errors/er403");
      }
  }

 function _rules(){
		$this->form_validation->set_rules('judul', 'judul', 'trim|xss_clean|required');
    $this->form_validation->set_rules('desc', 'Konten', 'trim|xss_clean|required');
    $this->form_validation->set_rules('seotitle', 'Title Seo', 'trim|xss_clean|required');
		$this->form_validation->set_rules('image', 'Gambar', 'trim|xss_clean');
    $this->form_validation->set_rules('kategori', 'Kategori', 'trim|xss_clean|required');
    $this->form_validation->set_rules('komentar', 'Komentar', 'trim|xss_clean|required|numeric');
		$this->form_validation->set_error_delimiters('<span class="text-danger">', '</span>');
  }

 function index(){
    $this->layouts->set_title('Berita');
    $this->layouts->view(config_item("cpanel").'content/berita/index');
  }

function json() {
    header('Content-Type: application/json');
    echo $this->model->json();
}


 function detail($id){
