 function tambah($status=''){
    if ($status=='aksi') {
        $json = array('success'=>false ,'alert'=>array());
        if ($this->input->is_ajax_request()) {
            $this->_rules();
            if ($this->form_validation->run()) {
                $insert = array(
																'judul' => $this->input->post('judul',TRUE),
																'desc' => $this->input->post('desc',TRUE),
																'image' => $this->input->post('image',TRUE),
																'slug' => slug($this->input->post('seotitle',TRUE)),
                                'komentar' => $this->input->post('komentar',TRUE),
                                'id_kategori' => $this->input->post('kategori',TRUE),
																'created_by' => sess('id_auth'),
																'created_at' => date('Y-m-d h:i:s'),
															);
                $this->model->get_insert($insert);
                $json['alert']   = 'Berhasil Menyimpan!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      $this->layouts->set_title('Berita');
      $data = array('layout_title' => 'Berita',
                      'button'=>'tambah',
                      'aksi' =>site_url(config_item("cpanel").'berita/tambah/aksi'),
											'id_berita' => set_value('id_berita'),
											'judul' => set_value('judul'),
											'desc' => set_value('desc'),
											'image' => set_value('image'),
                      'slug'  => set_value('slug'),
                      'komentar'=>set_value('komentar'),
											'kategori' => set_value('kategori'),
											);
			 $this->layouts->view(config_item("cpanel").'content/berita/form',array(),$data);
  //MODAL TAMBAH
  //$this->layouts->view(config_item("cpanel").'content/berita/form',array(),$data,false);
     }
  }

 function edit($id,$status=''){
      if ($status=='aksi') {
          $json = array('success'=>false ,'alert'=>array());
          if ($this->input->is_ajax_request()) {
              $this->_rules();
              if ($this->form_validation->run()) {
                  $update = array(
																'judul' => $this->input->post('judul',TRUE),
																'desc' => $this->input->post('desc',TRUE),
																'image' => $this->input->post('image',TRUE),
																'slug' => slug($this->input->post('seotitle',TRUE)),
                                'id_kategori' => $this->input->post('kategori',TRUE),
                                'komentar' => $this->input->post('komentar',TRUE),
																'update_by' => sess('id_auth'),
																'update_at' => date('Y-m-d h:i:s'),
															);
                $this->model->get_update($id,$update);
                $json['alert']   = 'Berhasil Mengedit!';
                $json['success'] = true;
            }else{
                foreach ($_POST as $key => $value) {
                  $json['alert'][$key] = form_error($key);
                }
            }
            echo json_encode($json);
        }
    }else{
      if($row=$this->model->get_where($id)){
        $this->layouts->set_title('Berita');
        $data = array('layout_title' => 'Berita',
                      'button'=>'edit',
                      'aksi' =>site_url(config_item("cpanel").'berita/edit/'.$id.'/aksi'),
											'id_berita' => set_value('id_berita', $row->id_berita),
											'judul' => set_value('judul', $row->judul),
											'desc' => set_value('desc', $row->desc),
											'image' => set_value('image', $row->image),
                      'slug'  => set_value('slug', $row->slug),
                      'komentar'=>set_value('komentar',$row->komentar),
                      'kategori' => set_value('kategori', $row->id_kategori),
											);
			 $this->layouts->view(config_item("cpanel").'content/berita/form',array(),$data);
  //MODAL EDIT
  //$this->layouts->view(config_item("cpanel").'content/berita/form',array(),$data,false);
			}else{
      $this->error_404();
    }
  }
}

 function hapus($id){
  if ($this->input->is_ajax_request()) {
      $this->model->get_delete($id);
      $data['alert'] = 'Berhasil menghapus!';
      echo json_encode($data);
    }
}

